import './App.css';
import Logo from "./girlmain.png";
import { useState } from 'react';

function App() {
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");
  const ops = ['/', '*', '+', '-', '.'];
  const updateCalc = value => {
    if (
      (ops.includes(value) && calc === '') || (ops.includes(value) && ops.includes(calc.slice(-1)))
    ) {
      return;
    }
    setCalc(calc + value);
    if (!ops.includes(value)) {
      setResult(eval(calc + value).toString())
    }
  }
  const calculate = () => {
    setCalc(eval(calc).toString());
  }
  const deleteLast = () => {
    if (calc === '') {
      return;
    }
    const value = calc.slice(0, -1);
    setCalc(value);
  }
  const createDigits = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button
          key={i}
          onClick={() => updateCalc(i.toString())}>
          {i}
        </button>
      )
    }
    return digits;
  }
  return (
    <div className='Calculator1'>
      <span className="nav">You don't have to be an mathematician to have a feel for numbers ! Keep calm and calculate on !! calcii !!!</span>
      <div className="main">
        <img src={Logo} className='girlmain' alt="logo" />
        <div className="content">
          <span className="Heading">
              BCalculator 
          </span>

          <div className="calculator">
            <div className="display">
              {result ? <span>({result})</span> : ''}&nbsp;
              {calc || '0'}
            </div>
            <div className="operators">
              <button onClick={() => updateCalc('/')}>/</button>
              <button onClick={() => updateCalc('*')}>*</button>
              <button onClick={() => updateCalc('+')}>+</button>
              <button onClick={() => updateCalc('-')}>-</button>

              <button onClick={deleteLast}>DEL</button>
            </div>
            <div className="digits">
              {createDigits()}
              <button onClick={() => updateCalc('0')}>0</button>
              <button onClick={() => updateCalc('.')}>.</button>
              <button onClick={calculate}>=</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
